import React from "react";
import ImageList from "@mui/material/ImageList";
import ImageListItem from "@mui/material/ImageListItem";
import "./Body.css";

import Kim from "../../assets/kimLarsen.jpeg";
import Robbie from "../../assets/robbie.jpeg";
import ShuBiDua from "../../assets/shuBiDua.jpeg";
import TV2 from "../../assets/tv2.jpeg";
import Helmig from "../../assets/helmig.jpg";
import Danseorkestret from "../../assets/danseorkestret.jpeg";

function RepBody() {
  function ShowAndHide() {
    var x = document.getElementById("cut-text");
    var y = document.getElementById("dots");
    if (x.style.visibility === "hidden") {
      x.style.visibility = "visible";
      x.style.whiteSpace = "normal";
      y.style.visibility = "hidden";
    } else {
      x.style.visibility = "hidden";
      x.style.whiteSpace = "nowrap";
    }
  }

  return (
    <div
      style={{
        backgroundColor: "rgba(0, 0, 0, 0.8)",
        borderRadius: "10px",
        maxWidth: "1200px",
        width: "90%",
        position: "relative",
        display: "flex",
        flexWrap: "wrap",
        overflow: "hidden",
        marginTop: 7,
        marginBottom: 27,
        justifyContent: "center",
        border: "1px solid rgba(0, 0, 0, 0.2)",
      }}
      className="section"
    >
      {document.documentElement.clientWidth < 720 ? (
        <div className="promo">
          <h1 style={{ fontSize: "18px" }}>
            SE VORES PROMOVIDEO{" "}
            <a href="https://stackoverflow.com/questions/39041960/how-to-unmute-html5-video-with-a-muted-prop">
              HER
            </a>
          </h1>
        </div>
      ) : (
        <div className="promo">
          <h1 style={{ fontSize: "30px" }}>
            SE VORES PROMOVIDEO{" "}
            <a href="https://stackoverflow.com/questions/39041960/how-to-unmute-html5-video-with-a-muted-prop">
              HER
            </a>
          </h1>
        </div>
      )}
      {document.documentElement.clientWidth < 720 ? (
        <div className="textfield">
          <p style={{ fontSize: "16px" }}>
            Roots in Suits har et stort og varieret repertoire.
            <p
              onClick={ShowAndHide}
              id="dots"
              style={{ fontSize: "15px", color: "rgb(117,117,117)" }}
            >
              Læs mere...
            </p>
            <span id="cut-text">
              Vi har stort fokus på at "ramme" vores publikum hver gang. Derfor
              tilpasser vi løbende vores repertoire og imødekommer også gerne
              ønsker fra publikum. Af samme grund har vi ikke en fast sætliste,
              men vi spiller, blandt mange, mange andre, disse numre:
            </span>
          </p>
        </div>
      ) : (
        <div className="textfield">
          <p style={{ fontSize: "25px" }}>
            Roots in Suits har et stort og varieret repertoire. <br />
            Vi har stort fokus på at "ramme" vores publikum hver gang. Derfor
            tilpasser vi løbende vores repertoire og imødekommer også gerne
            ønsker fra publikum. Af samme grund har vi ikke en fast sætliste,
            men vi spiller, blandt mange, mange andre, disse numre:
          </p>
        </div>
      )}
      {document.documentElement.clientWidth < 720 ? (
        <ImageList
          sx={{ width: "90%", height: "80vh" }}
          cols={2}
          rowHeight={1}
          gap={7}
          className="gallery"
        >
          {itemData.map((item) => (
            <ImageListItem key={item.img}>
              <img
                src={`${item.img}?w=164&h=164&fit=crop&auto=format`}
                srcSet={`${item.img}?w=164&h=164&fit=crop&auto=format&dpr=2 2x`}
                alt={item.title}
                loading="lazy"
              />
            </ImageListItem>
          ))}
        </ImageList>
      ) : (
        <ImageList
          sx={{ width: "90%", height: "90vh" }}
          cols={3}
          rowHeight={1}
          gap={17}
          className="gallery"
        >
          {itemData.map((item) => (
            <ImageListItem key={item.img}>
              <img
                src={`${item.img}?w=164&h=164&fit=crop&auto=format`}
                srcSet={`${item.img}?w=164&h=164&fit=crop&auto=format&dpr=2 2x`}
                alt={item.title}
                loading="lazy"
              />
            </ImageListItem>
          ))}
        </ImageList>
      )}
    </div>
  );
}

const itemData = [
  {
    img: Kim,
    title: "Kim Larsen",
  },
  {
    img: Robbie,
    title: "Robbie Williams",
  },
  {
    img: Helmig,
    title: "Thomas Helmig",
  },
  {
    img: TV2,
    title: "TV2",
  },
  {
    img: Danseorkestret,
    title: "Danseorkestret",
  },
  {
    img: ShuBiDua,
    title: "ShuBiDua",
  },
];

export default RepBody;
